#!/bin/sh
EOULSAN_DIR=~/eoulsan-0.1 CMD=$EOULSAN_DIR/eoulsan.sh
$CMD createsoapindex design.txt $CMD filterreads design.txt $CMD soapmapreads design.txt $CMD filtersamples --threshold 50 design.txt design_filtered.txt $CMD expression --tmpdir . design-filtered.txt $CMD anadiff design_filtered2.txt
